    @Test
    public void trocaDePlanoComProblemaNoInicioDaTrocaDePlano() throws IOException {
        inicioExecucao = new DateTime(2016, 12, 19, 7, 59, 0);
        controlador = getControladorInicio();
        Motor motor = new Motor(controlador, inicioExecucao, this);

        avancarSegundos(motor, 300);

        int offsetEstagio = 62;
        int anel1 = 1;

        assertEquals("Estágio 2", 2, listaEstagios.get(inicioExecucao.plusSeconds(offsetEstagio)).get(anel1).getEstagio().getPosicao().intValue());
        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 1, 0, 6000, EstadoGrupoSemaforico.VERMELHO));
        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 1, 6000, 16000, EstadoGrupoSemaforico.VERMELHO));

        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 2, 0, 6000, EstadoGrupoSemaforico.VERMELHO));
        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 2, 6000, 16000, EstadoGrupoSemaforico.VERDE));

        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 3, 0, 3000, EstadoGrupoSemaforico.VERMELHO_INTERMITENTE));
        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 3, 3000, 6000, EstadoGrupoSemaforico.VERMELHO_LIMPEZA));
        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 3, 6000, 16000, EstadoGrupoSemaforico.VERMELHO));

        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 4, 0, 6000, EstadoGrupoSemaforico.VERDE));
        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 4, 6000, 16000, EstadoGrupoSemaforico.VERDE));

        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 5, 0, 3000, EstadoGrupoSemaforico.VERMELHO_INTERMITENTE));
        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 5, 3000, 6000, EstadoGrupoSemaforico.VERMELHO_LIMPEZA));
        verificaGruposSemaforicos(offsetEstagio, new GrupoCheck(1, 5, 6000, 16000, EstadoGrupoSemaforico.VERMELHO));
    }

